import './App.css';
import React, { useEffect, useState, useCallback, useMemo, useContext, createContext } from 'react';
import ResourceData from './ResourceData';
import List from './List';
import AgeChecker from './AgeChecker';


export default function App() {
  const [resourceType, setresourceType] = useState('posts')
  const [number, setNumber] = useState(1)
  const [dark, setDark] = useState(false)
  const UserContext = createContext()
  const [count, setCount] = useState(0)
  const [inputValue, setInputValue] = useState("")
  const refNumber = useRef(0)

  var doubleNumber = useMemo(() => slowFunction(number), [number])


  const themeStyle = useMemo(() => {
    return({
      backgroundColor: dark ? 'black' : 'white',
      color: dark ? 'white' : 'black'
    })
  }, [dark])

  useEffect(() => {
    console.log('Theme changed: ' + (dark ? 'black' : 'White'))
  }, [themeStyle])

  const getItems = useCallback(() => {
    return [number, number + 1, number + 2]
  }, [number])

  useEffect(() => {
    var timer = setTimeout(() => {
      setCount((count) => count + 1)
    }, 1000)

    return () => clearTimeout(timer)
  }, [])

  return (
    <>
    <UserContext.Provider value={ count }>
    <div style={themeStyle}>
      <button onClick={() => setresourceType('posts')}>Posts</button>
      <button onClick={() => setresourceType('comments')}>Comments</button>
      <button onClick={() => setresourceType('albums')}>Albums</button>
      <h1>{resourceType}</h1>
      <h1><ResourceData resourceType={resourceType} /></h1>
      <input type="number" value={number} onChange={e => setNumber(parseInt(e.target.value))}/>
      <button onClick={() => setDark(prevDark => !prevDark)}>Change Theme</button>
      <div>{ doubleNumber }</div>
      <List getItems={getItems} />
      <AgeChecker number={number} />
    <p>Coount: { count }</p>
    <input type="text" value={ inputValue } onChange={(e) => setInputValue(e.target.value)} />
    Render count: {count.current}
    </div>
    </UserContext.Provider>
    </>
  );
}


function slowFunction(num){
  for(var i = 0; i < 1000000000; i++){}
  return num*2;
}
