import React, {
  useEffect,
  useState,
  useCallback,
  useMemo,
  useContext,
  createContext,
  useRef,
} from "react";

export default function UseRefTest() {
  const [inputValue, setInputValue] = useState("");
  const refNumber = useRef(0);

  useEffect(() => {
    refNumber.current = refNumber.current + 1;
  }, [inputValue]);

  return (
    <>
      <p>
        <input type="text" placeholder="Put focus on me..." />
        <button>Use me to put focus</button>
      </p>
      <input
        type="number"
        value={inputValue}
        onChange={(f) => setInputValue(f.target.value)}
      />
      Render count: {refNumber.current}
    </>
  );
}
