import React, { useEffect, useState, useCallback, useMemo, useContext, createContext, useRef } from 'react';


export default function UseRefTest({ count }) {
    const [inputValue, setInputValue] = useState("")
    const refNumber = useRef(0)

    useEffect(() => {
        refNumber.current = refNumber.current + 1;
      }, [inputValue])

    return(
    <>
    <input type="text" value={ inputValue } onChange={(f) => setInputValue(f.target.value)} />
    Render count: {refNumber.current}
    </>
    )
    }
