import React, { useEffect, useState, useCallback, useMemo, useContext, createContext, useRef } from 'react';


export default function UseRefTest() {
    const [inputValue, setInputValue] = useState("")
    const refNumber = useRef(0)
    const inputElement = useRef()

    const focusInput = () => {
        inputElement.current.focus()
    }

    useEffect(() => {
        refNumber.current = refNumber.current + 1;
      }, [inputValue])

    return(
    <>
    <p><input type="text" ref={inputElement} />
    <button onClick={focusInput}>Focus Input</button></p>
    <input type="number" value={ inputValue } onChange={(f) => setInputValue(f.target.value)} />
    Render count: {refNumber.current}
    </>
    )
    }
