import React, { useState } from "react";
export default function ReactForms() {
  const [inputs, setInputs] = useState({});
  const [buttonValue, setButtonValue] = useState("Submit");

  const formSubmit = (e) => {
    e.preventDefault();
    setButtonValue(inputs.name);
    alert(inputs.name + ": " + inputs.age);
  };

  const handleChange = (e) => {
    const name = e.target.name;
    const value = e.target.value;
    setInputs((values) => ({ ...values, [name]: value }));
  };
  return (
    <>
      <form onSubmit={formSubmit}>
        <label>
          Enter your name:
          <input
            type="text"
            name="name"
            value={inputs?.name}
            onChange={handleChange}
          />
        </label>
        Enter your age:
        <label>
          <input
            type="number"
            name="age"
            onChange={handleChange}
            value={inputs?.age}
          />
        </label>
        <textarea value={ inputs.text } onChange={ handleChange } />
        <button type="submit">{buttonValue ? buttonValue : "Submit"}</button>
      </form>
    </>
  );
}
