import React, { useState } from "react";
export default function ReactForms() {
  const [inputs, setInputs] = useState({});
  const [buttonValue, setButtonValue] = useState("Submit");

  const formSubmit = (e) => {
    e.preventDefault();
    setButtonValue(inputs.name);
    alert(inputs.name + ": " + inputs.age);
    console.table(inputs)
  };

  const handleChange = (e) => {
    const name = e.target.name;
    const value = e.target.value;
    const text = e.target.text;
    setInputs((values) => ({ ...values, [name]: value, "text": [text] }));
  };
  return (
    <>
      <ul>
        <form onSubmit={formSubmit}>
          <li>
            <label>
              Enter your name:
              <input
                type="text"
                name="name"
                value={inputs?.name}
                onChange={handleChange}
              />
            </label>
          </li>
          <li>
            <label>
              Enter your age:
              <input
                type="number"
                name="age"
                onChange={handleChange}
                value={inputs?.age}
              />
            </label>
          </li>
          <li>
            <textarea value={inputs.text} onChange={handleChange} />
          </li>
          <button type="submit">{buttonValue ? buttonValue : "Submit"}</button>
        </form>
      </ul>
    </>
  );
}
